export class HomePage extends Component{
    constructor(props){
        super(props)
           
    }
   
    render(){
        const styling = {
            color: "black",
            backgroundColor: "lightblue",
            padding: "6px",
            fontFamily: "Arial"
          };
        return(
            <div>
                <h1 style={{color: "white", backgroundColor: "black"}}>Welcome to React!</h1>
      <p style={styling}> 
      React is a free and open-source front-end JavaScript library for building user interfaces based on UI components. It is maintained by Meta and a community of individual developers and companies. React can be used as a base in the development of single-page or mobile applications.</p>
         <body>
        <h bold>React PreRequisites :</h><br /> Before you can code using React, you need to learn HTML, CSS, and JavaScript. While HTML and CSS are not required, the JSX syntax used by React is based on HTML. You'll need to have a good knowledge of HTML to understand how it works
         </body>
            </div>
        )

    }
}

export default HomePage
