class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogginActive: false
    };
  }
  handleLoginEvent=event=>{
    this.setState({isLogginActive: event.target.value})
  }
  render() {
    const { isLogginActive } = this.state;
    console.log("isLogginActive", isLogginActive)
    return (
      <div className="App">
        <div className="login">
          <div className="container" >
            {!isLogginActive && <LoginForm  handleLoginEvent={this.handleLoginEvent}/>}
            {isLogginActive && <HomePage  />}

          </div>
        </div>
      </div>
    );
  }
}
export default App;
