import React, {Component} from 'react'
import './HomePage.css';
import './reactUsage.jpg';
import MenuItem from "@material-ui/core/MenuItem";
import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";

export class HomePage extends Component{
    constructor(props){
        super(props)
        this.state = {
            toggleMenu : false,
            toggleMenuHome: false,
            toggleMenuProfile: false
        }
           
    }
    handleCloseHome =()=>{
        this.setState({toggleMenuHome: true, toggleMenu: false})
    }
    handleClickProfile =()=>{
        this.setState({toggleMenuProfile: true, toggleMenu:false})
    }
    handleClick = ()=>{
        this.setState({toggleMenu: true})
    }
   
    render(){
        const {toggleMenu, toggleMenuHome, toggleMenuProfile} = this.state;
        console.log("Me",toggleMenu )
        const styling = {
            color: "black",
            backgroundColor: "lightblue",
            padding: "6px",
            fontFamily: "Arial"
          };
        return(
            <div>
<div style={{marginRight: "100%"}}>
<button onClick={this.handleClick}>Menu  </button>
<span style={{paddingTop: "10px"}}><Menu
		keepMounted
		toggleMenu={toggleMenu}
		onClose={this.handleClose}
		open={Boolean(toggleMenu)}
        PaperProps={{
            style: {
              maxHeight: `500px`,
               marginTop:'-3.5%'
            }
          }}
	>
		<MenuItem onClick={this.handleCloseHome} divider={false}>Home</MenuItem>
		<MenuItem onClick={this.handleClickProfile} >Profile</MenuItem>
		<MenuItem onClick={this.handleCloseHome} >Logout</MenuItem>
	</Menu></span>
    </div>
<div>
{!toggleMenuHome && !toggleMenuProfile && <><h1 style={{color: "white", backgroundColor: "black"}}>Please click the menu</h1></>}
        {toggleMenuHome &&   <span>   <h1 style={{color: "white", backgroundColor: "black"}}>Welcome to React!</h1>
      <p style={styling}> 
      React is a free and open-source front-end JavaScript library for building user interfaces based on UI components. It is maintained by Meta and a community of individual developers and companies. React can be used as a base in the development of single-page or mobile applications.</p>
         <body>
        <h>React PreRequisites :</h><br /> Before you can code using React, you need to learn HTML, CSS, and JavaScript. While HTML and CSS are not required, the JSX syntax used by React is based on HTML. You'll need to have a good knowledge of HTML to understand how it works
         </body> </span>}</div>
         {toggleMenuProfile && <span> <h1 style={{color: "white", backgroundColor: "black"}}>Welcome to your profile</h1>
         <img src={require('./reactUsage.jpeg')} /></span>}
            </div>
        )

    }
}

export default HomePage
